function newItem() {
  let t = $("#list"),
    n = $("#input").val(),
    e = $("<li></li>");
  e.append(n),
    "" === n ? alert("You must write something!") : t.append(e),
    e.on("dblclick", function () {
      e.toggleClass("strike");
    });
  let l = $("<button></button>");
  l.text("X"),
    e.append(l),
    l.on("click", function () {
      e.addClass("delete");
    }),
    t.sortable(),
    $("#input").val("");
}
